mapper.update:
update `vkc`.`leads` set
`name` = #{name},
source = #{source}, investPartner = #{investPartner}, manager = #{manager},
company = #{company},
industry = #{industry}, `location` = #{location}, summary = #{summary}, description = #{description}, `status` = #{status}, last_change = #{lastChange}, `next` = #{next}
, `source_reason` = #{sourceReason}
, `pass_reason` = #{passReason}
where uid = #{uid}

mapper.select:
select *
from
`vkc`.`leads`
where block_flag = 0

    and `name` = #{name}


    and `source` = #{source}


    and `investPartner` = #{investPartner}


    and `manager` = #{manager}


    and `company` = #{company}


    and `industry` = #{industry}


    and `location` = #{location}


    and `status` = #{status}


    and `last_change` = #{lastChange}


    and `next` = #{next}



      and `status` <= #{statusLessAndEqual}




      and `status` > #{statusBiggerThan}


order by last_change
